import './App.css';
import { useState } from 'react'

function App() {

  const [events, setEvents] = useState([
    {title:" mario's", id:"001"},
    {title:" broser's", id:"002"},
    {title:" race's", id:"003"}
  ])

  const handleClickBad = (id) => {
    setEvents(events.filter((event)=>{
      return event.id !== id
    }))
  }

  const handleClickGood = (id) => {
    // prevState => newState
    setEvents((prevEvents)=>{
      prevEvents.filter((event)=>{
        return event.id !== id
      })
    })
  }

  return (
    <div className="App">
      {events.map((event, index) => (
        <div key={event.id}>
          <h2>{index} - {event.title}</h2>
          <button onClick={()=>handleClickBad(event.id)}>
            delete events 1
          </button>
          <button onClick={()=>handleClickGood(event.id)}>
            delete events 2
          </button>
        </div>
      ))
      }
    </div>
  );
}

// always export components to be rendered!
export default App;
