import './App.css';
import { useState } from 'react'
import Title from './components/Title';
import Modal from './components/Modal';

function App() {

  const [showEvents, setShowEvents] = useState(true)
  const [events, setEvents] = useState([
    {title:" mario's", id:"001"},
    {title:" broser's", id:"002"},
    {title:" race's", id:"003"}
  ])

  console.log(showEvents)

  const handleClickBad = (id) => {
    setEvents(events.filter((event)=>{
      return event.id !== id
    }))
  }

  const handleClickGood = (id) => {
    // prevState => newState
    setEvents((prevEvents)=>{
      return prevEvents.filter((event)=>{
        return event.id !== id
      })
    })
  }

  const sub = "All latest events in Marioland"

  return (
    <div className="App">
      <Modal/>

      <Title title="events in your area" subtitle={sub}/>

      <div>
      {showEvents && (
        <button onClick={() => setShowEvents(false)}>hide events</button>)}
      {!showEvents && (
        <button onClick={() => setShowEvents(true)}>show events</button>)}
      </div>

      { showEvents && events.map((event, index) => (
        <div key={event.id}>
          <h2>{index} - {event.title}</h2>
          <button onClick={()=>handleClickBad(event.id)}>
            delete events method #1
          </button>
          <button onClick={()=>handleClickGood(event.id)}>
            delete events method #2
          </button>
        </div>
      ))}
    </div>
  );
}

// always export components to be rendered!
export default App;
