import './App.css';
import { useState } from 'react'

function App() {

  const [name, setName] = useState('mario')
  const [events, setEvents] = useState([
    {title: " mario's", id:1},
    {title: " broser's", id:2},
    {title: " race's", id:3}
  ])

  const handleClick = () => {
    setName('luigi'); // reevaluate the component function -> re-inject componet to template
    console.log(name); // console log runs before setName by React Default
  }

  return (
    <div className="App">
      <h1>My name is {name}</h1>
      <button onClick={handleClick}>Change Name</button>
    </div>
  );
}

// always export components to be rendered!
export default App;
